import React from 'react';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            projectList: ['eaturveg', 'Spotify Recently Added', 'Filmotography'],
            selectedProject: null
        };

        this.selectProject = this.selectProject.bind(this);
        this.getProject = this.getProject.bind(this);
    }

    selectProject(e) {
        if (e) {
            const projectName = e.target.innerText.toLowerCase();
            this.state.setState({selectedProject: projectName});
        ;
    }
}

getProject()
{
    console.log('called getproject', this.state.selectedProject);
    if (this.state.selectedProject) {
        return (
            <div className="Project">
                <h1>{this.state.selectedProject}</h1>
                {/*{React.createElement(project.replace('', '-'), {})}*/}
            </div>);
    } else return (
        <div className="Project">
            <p className="home">
                Hi, welcome to my projects blog.<br/>
                <small>Select one from the left.</small>
            </p>
        </div>
    );
}

render()
{
    return (
        <div className="App">
            <div className="navigation">
                <ul>
                    <h1>Projects:</h1>
                    {this.state.projectList.map((project) => {
                        return (
                            <li key={project}>
                                <button className="project-button"
                                        onClick={this.selectProject}>{project}</button>
                            </li>
                        );
                    })}
                </ul>
            </div>
            <div className="content">
                {this.getProject()}
            </div>
        </div>
    );
}
}

export default App;
