import React from 'react';
import './App.css';
import ProjectView from "./components/ProjectView";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedProject: null
        };

        // this.handleClick = this.handleClick.bind(this);
    }

    handleClick(project) {
        // this.state.selectedProject = project;
    }

    getProject(project) {
        if(project === 'eaturveg') return (<div className="Project">
            <h1>eaturveg</h1>
        </div>);
        else if (project === 'spotify') return (<div className="Project">
            <h1>spotify</h1>
        </div>);
        else if (project === 'filmotography') return (<div className="Project">
            <h1>filmotography</h1>
        </div>);
    else return (
            <div className="Project">
                <p className="home">
                    Hi, welcome to my projects blog.<br/>
                    <small>Select one from the left.</small>
                </p>
            </div>
        );
    }

    render() {
        return (
            <div className="App">
                <div className="navigation">
                    <ul>
                        <h1>Projects:</h1>
                        <li>
                            <button className="project-button" onClick={this.getProject('eaturveg')}>eaturveg</button>
                        </li>
                        <li>
                            <button className="project-button" onClick={this.getProject('spotify')}>Spotify Recently
                                Added
                            </button>
                        </li>
                        <li>
                            <button className="project-button" onClick={this.getProject('filmotography')}>Filmotography
                            </button>
                        </li>
                    </ul>
                </div>
                <div className="content">
                    {this.getProject()}
                </div>
            </div>
        );
    }
}

export default App;
