import React from 'react';
import './App.css';
import ProjectView from "./project-view/ProjectView";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedProject: null
        };

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(project) {
        this.state.projects[project] = !this.state.projects[project];
    }

    render() {
        return (
            <div className="App">
                <div className="navigation">
                    <ul>
                        <h1>Projects:</h1>
                        <li>
                            <button className="project-button" onClick={this.handleClick('eaturveg')}>eaturveg</button>
                        </li>
                        <li>
                            <button className="project-button" onClick={this.handleClick('spotify')}>Spotify Recently
                                Added
                            </button>
                        </li>
                        <li>
                            <button className="project-button" onClick={this.handleClick('filmotography')}>Filmotography
                            </button>
                        </li>
                    </ul>
                </div>
                <div className="content">
                    <ProjectView project={this.state.selectedProject}/>
                </div>
            </div>
        )
    }
}

export default App;
