import React from 'react';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            projectList: ['eaturveg', 'Spotify Recently Added', 'Filmotography']
        }
    }
    getProject(e) {
        if (e) {
            return (
                <div className="Project">
                    <h1>{e.target.innerText.toLowerCase()}</h1>

                </div>);
            const project = e.target.innerText.toLowerCase();
            if (project.includes('eaturveg')) return (
                <div className="Project">
                    <h1>eaturveg</h1>
                </div>);
            else if (project.includes('spotify')) return (
                <div className="Project">
                    <h1>spotify</h1>
                </div>);
            else if (project.includes('filmotography')) return (
                <div className="Project">
                    <h1>filmotography</h1>
                </div>);
        } else return (
            <div className="Project">
                <p className="home">
                    Hi, welcome to my projects blog.<br/>
                    <small>Select one from the left.</small>
                </p>
            </div>
        );
    }

    render() {
        return (
            <div className="App">
                <div className="navigation">
                    <ul>
                        <h1>Projects:</h1>
                        {this.state.projectList.map((project) => {
                            return (
                                <li key={project}>
                                    <button className="project-button"
                                            onClick={this.getProject}>{project}</button>
                                </li>
                            );
                        })}
                    </ul>
                </div>
                <div className="content">
                    {this.getProject(null)}
                </div>
            </div>
        );
    }
}

export default App;
