import React from 'react';
import './App.css';
import ProjectView from "./components/ProjectView";

class App extends React.Component {
    getProject(e) {
        if (e) {
        const project = e.target.innerText.toLowerCase();
            if (project.includes('eaturveg')) return (
                <div className="Project">
                    <h1>eaturveg</h1>
                </div>);
            else if (project.includes('spotify')) return (
                <div className="Project">
                    <h1>spotify</h1>
                </div>);
            else if (project.includes('filmotography')) return (
                <div className="Project">
                    <h1>filmotography</h1>
                </div>);
        }
        else return (
                <div className="Project">
                    <p className="home">
                        Hi, welcome to my projects blog.<br/>
                        <small>Select one from the left.</small>
                    </p>
                </div>
            );
    }

    render() {
        const projectList = ['eaturveg', 'Spotify Recently Added', 'Filmotography'];
        return (
            <div className="App">
                <div className="navigation">
                    <ul>
                        <h1>Projects:</h1>
                        { projectList.map((project) => {
                            return (
                                <li>
                                    <button className="project-button" onClick={this.getProject}>{project}</button>
                                </li>
                            );
                        })}
                    </ul>
                </div>
                <div className="content">
                    {this.getProject(null)}
                </div>
            </div>
        );
    }
}

export default App;
